settings.css_section:
.section_id .slideshow__title{
  font-family: Jost !important;
  color: #999;
  font-size: 1.4rem;
  letter-spacing: -.01em;
  margin-bottom: .5rem;
}
.section_id .slideshow__subtitle{
  font-family: Jost !important;
  color: #222;
  font-size: 3rem;
  line-height: 1.2;
  letter-spacing: -.03em;
  margin-bottom: 1.2rem;
}
.section_id .slideshow__description{
  font-family: Jost !important;
  color: #fff;
  margin-bottom: 0;
  clear: both;
  font-size: 1.4rem;
  line-height: 2.4rem;
}
.section_id .slideshow__subtitle sup {
  vertical-align: top;
  font-size: 1.3rem;
  top: 1em;
  margin-right: 0;
  letter-spacing: -.01em;
}
@media (min-width: 576px){
  .section_id .slideshow__title{
    font-size: 1.6rem;
  }
  .section_id .slideshow__subtitle{
    font-size: 3.6rem;
    margin-bottom: 1.3rem;
  }
}
@media (min-width: 768px){
  .section_id .slideshow__title{
    font-size: 1.6rem;
  }
  .section_id .slideshow__subtitle{
    font-size: 4.2rem;
    margin-bottom: 1.7rem;
  }
  .section_id .slideshow__subtitle sup {
    font-size: 1.6rem;
  }
  .section_id .slideshow__description{
    line-height: 3.6rem;
    font-size: 2.4rem;
    letter-spacing: 0.05em;
  }
}
@media (min-width: 992px){
  .section_id .slideshow__subtitle sup {
    font-size: 1.8rem;
  }
}
@media (min-width: 1200px){
  .section_id .slideshow__title{
    font-size: 1.6rem;
  }
  .section_id .slideshow__subtitle{
    font-size: 6.6rem;
  }
  .section_id .slideshow__subtitle sup {
    font-size: 2rem;
  }
}
