settings.css_section:
.section_id .slideshow__title{
  color: #fff;
  font-size: 2rem;
  letter-spacing: -.025em;
  margin-bottom: .5rem;
}
.section_id .slideshow__subtitle{
  color: #fff;
  font-size: 4rem;
  line-height: 1.1;
  letter-spacing: .1em;
  margin-bottom: 1.2rem;
  text-transform: uppercase;
}
.section_id .slideshow__description{
  color: #fff;
  margin-bottom: 0;
  clear: both;
  font-size: 1.4rem;
  line-height: 2.4rem;
}
@media (min-width: 768px){
  .section_id .slideshow__title{
    font-size: 2.4rem;
  }
  .section_id .slideshow__subtitle{
    font-size: 6rem;
    margin-bottom: 1.4rem;
    letter-spacing: .2em;
  }
  .section_id .slideshow__description{
    line-height: 3.6rem;
    font-size: 2.4rem;
    letter-spacing: 0.05em;
  }
}
@media (min-width: 992px){
  .section_id .slideshow__title{
    font-size: 2.48rem;
  }
  .section_id .slideshow__subtitle{
    font-size: 7.6rem;
    margin-bottom: 1.8rem;
  }
}
@media (min-width: 1200px){
  .section_id .slideshow__title{
    font-size: 3.2rem;
  }
  .section_id .slideshow__subtitle{
    font-size: 9rem;
    margin-bottom: 2.2rem;
  }
}
