current.custom_css:
.header-bottom .main-mn &gt; li:first-child &gt; a:before {
    left: -1.5rem;
}
.icon-box-side {
    font-size: 1.4rem;
}
.icon-box-side .icon-box-title {
    text-transform: uppercase;
    font-weight: 400;
    font-size: 1.8rem;
}
.md_products_section .tab-product.none-big-title .box-tabs a{
  text-transform: uppercase;
  color: #999;
}
.md_products_section .box-tabs a:not(.active){
  color: var(--text);
}
.md_products_section .box-tabs a.active {
  color: var(--primary);
}
.section-title h3{
  font-weight: 700;
  letter-spacing: .01em;
  text-transform: uppercase;
}
.btn.button-more {
  border-width: .2rem;
}
footer .widget-about-title{
  color: #fff
}

settings.css_section:
.section_id .slideshow__title{
  font-size: 1.8rem;
  letter-spacing: .1em;
  margin-bottom: .4rem;
  color: #777;
  text-transform: uppercase;
}
.section_id .slideshow__subtitle{
  color: #222;
  font-size: 5rem;
  line-height: .9;
  letter-spacing: -.03em;
  margin-bottom: 0;
  text-transform: uppercase;
}
.section_id .slideshow__subtitle strong{
  font-size: 7rem;
  font-weight: 700;
  letter-spacing: -.05em;
}
@media (min-width: 576px){
  .section_id .slideshow__subtitle{
    font-size: 7rem;
  }
  .section_id .slideshow__subtitle strong{
    font-size: 9rem;
  }
}
@media (min-width: 768px){
  .section_id .slideshow__subtitle{
    font-size: 8rem;
  }
  .section_id .slideshow__subtitle strong{
    font-size: 10rem;
  }
}
@media (min-width: 992px){
  .section_id .slideshow__subtitle{
    font-size: 9rem;
  }
  .section_id .slideshow__subtitle strong{
    font-size: 11rem;
  }
}
@media (min-width: 1200px){
  .section_id .slideshow__subtitle{
    font-size: 12rem;
  }
  .section_id .slideshow__subtitle strong{
    font-size: 18rem;
  }
}
.section_id .slideshow__description{
  font-size: 1.8rem;
  letter-spacing: .1em;
  margin-bottom: 2rem;
  color: #777;
  text-transform: uppercase;
}
.section_id .slideshow__button.list-inline-item .btn {
  border-color: #222;
  border-width: .2rem;
  min-width: 18rem;
}
.section_id .slideshow__button.list-inline-item .btn:hover {
  border-color: #222;
  background-color: #222;
}

.section_id .slideshow__title{
  color: #fff;
  font-size: 1.4rem;
  letter-spacing: .1em;
  text-transform: uppercase;
  margin-bottom: .6rem;
}
.section_id .slideshow__subtitle{
  color: #fff;
  font-size: 5rem;
  line-height: 1.15;
  letter-spacing: .01em;
  margin-bottom: 1rem;
}
.section_id .slideshow__description{
  color: #fff;
  letter-spacing: 0;
  font-size: 1.3rem;
  line-height: 1.8em;
  margin-bottom: 3.3rem;
}
