current.custom_css:
.header-bottom .main-mn &gt; li:first-child &gt; a:before {
  left: -1.5rem;
}
.icon-box{
  font-size: 1.6rem;
}
.icon-box-icon {
  font-size: 3.4rem;
}
.cta-title {
  font-size: 2.4rem;
}
.cta-desc {
  font-weight: 300;
}
.cta-desc span {
  color: #333;
}
.header.style-05 .header-bottom .main-mn&gt;li&gt;a:before {
 bottom: 0;
 height: 2px;
}
.md_products_section .box-tabs a{
  font-weight: 600;
}
.md_products_section .box-tabs a.active {
color: $color_body_text;
}

settings.css_section:
.section_id .slideshow__title{
  color: var(--primary);
  font-size: 1.4rem;
  letter-spacing: -.01em;
  margin-bottom: .5rem;
}
.section_id .slideshow__subtitle{
  color: #fff;
  font-size: 2.4rem;
  line-height: 1.2;
  letter-spacing: -.03em;
}
.section_id .slideshow__description{
  color: #fff;
  margin-bottom: 1rem;
  clear: both;
  font-size: 1.4rem;
  line-height: 2.4rem;
}
@media (min-width: 576px){
  .section_id .slideshow__title{
    font-size: 1.6rem;
  }
  .section_id .slideshow__subtitle{
    font-size: 3.6rem;
  }
}
@media (min-width: 768px){
  .section_id .slideshow__title{
    font-size: 1.8rem;
  }
  .section_id .slideshow__subtitle{
    font-size: 4.2rem;
  }
  .section_id .slideshow__description{
    line-height: 3.6rem;
    font-size: 2.4rem;
    letter-spacing: 0.05em;
  }
}
@media (min-width: 1200px){
  .section_id .slideshow__title{
    font-size: 2rem;
  }
  .section_id .slideshow__subtitle{
    font-size: 5.2rem;
  }
}
