current.custom_css:
.header-bottom .main-mn &gt; li:first-child &gt; a:before {
    left: -1.5rem;
}
.md_products_section .box-tabs a.active {
  color: $color_body_text;
  border-bottom-color: $color_primary;
}
.section-title h3{
  margin-bottom: 1.7rem;
}
.button-more.btn-outline-primary-2:not(:hover):not(:focus) {
  color: #333;
}
.button-more.btn-outline-primary-2:not(:hover):not(:focus) i {
  color: #333;
}
.header.style-05 .header-bottom .main-mn&gt;li&gt;a:before {
  bottom: 0;
  height: 2px;
}
.p-style-07 .product {
background-color: transparent;
}

settings.css_section:
.section_id .btn-outline-primary-2,
.section_id .btn-outline-primary-2 i {
  color: #fff;
}
.section_id .slideshow__title{
  color: #fff;
  font-weight: 300;
  font-size: 1.4rem;
  letter-spacing: -.025em;
  margin-bottom: 1rem;
}
.section_id .slideshow__subtitle{
  color: #fff;
  font-size: 3rem;
  line-height: 1.2;
  letter-spacing: 0;
  margin-bottom: 1.8rem;
}
@media (max-width: 767px){
  .section_id .btn {
    padding: .65rem 1rem;
  }
}
@media (min-width: 576px){
  .section_id .slideshow__title{
    font-size: 1.6rem;
  }
  .section_id .slideshow__subtitle{
    font-size: 3.6rem;
  }
}
@media (min-width: 768px){
  .section_id .slideshow__subtitle{
    font-size: 4.2rem;
  }
}
@media (min-width: 1200px){
  .section_id .btn {
    min-width: 19rem;
    padding-top: 1.15rem;
    padding-bottom: 1.15rem;
  }
  .section_id .slideshow__subtitle{
    font-size: 5rem;
  }
}
