current.custom_css:
.header-bottom .main-mn &gt; li:first-child &gt; a:before {
  left: -1.5rem;
}
.slideshow__button.list-inline-item .btn {
  min-width: auto;
  color: #fff;
  border-width: 2px;
}
.section-title .sub-title{
 margin-top: 1.5rem;
}
.cta.design_15 .cta-title {
  font-weight: 400;
}
.icon-boxes-container [class*="col-"]+[class*="col-"]:before {
  content: '';
  display: block;
  width: .1rem;
  height: 5.3rem;
  background-color: #e9e9e9;
  position: absolute;
  left: -.7rem;
  top: 50%;
  margin-top: -2.55rem;
}
.md_blog .item-blog .entry-title {
  font-size: 2rem !important;
  font-weight: 800 !important;
}
.p-style-15 .product-name {
  font-size: 1.5rem;
  font-weight: 600;
  text-transform: uppercase;
}
@media (min-width: 1430px){
:root {
    --max-container: 1400px;
}
}

settings.css_section:
.section_id .slideshow__title{
  color: #666;
  font-size: 2.4rem;
  letter-spacing: -.025em;
  margin-bottom: .7rem;
  text-indent: .2rem;
}
.section_id .slideshow__subtitle{
  color: #fff;
  font-size: 3rem;
  letter-spacing: -.025em;
  margin-bottom: 0;
  line-height: 1em;
}
.section_id .slideshow__description{
  color: #666;
  font-size: 2.4rem;
  letter-spacing: -.025em;
  margin-bottom: .7rem;
  text-indent: .2rem;
}
@media (min-width: 768px){
  .section_id .slideshow__subtitle{
    font-size: 6rem;
  }
  .section_id .slideshow__description{
    font-size: 3.4rem;
  }
}
