settings.css_section:
.section_id .slideshow__title{
  color: #fff;
  font-size: 1.3rem;
  letter-spacing: .1em;
  margin-bottom: 0rem;
  text-transform: uppercase;
}
.section_id .slideshow__subtitle{
  color: #fff;
  font-size: 6rem;
  line-height: 1.1;
  margin-bottom: 3rem;
}
.section_id a.btn {
  padding: .8rem 3.3rem;
}
.section_id .content-buttons {
  max-width: 1400px;
  margin: auto;
}
@media (min-width: 768px){  
.section_id .slideshow__subtitle{
  font-size: 10rem;
}
.section_id .content-buttons .swiper-button-prev,
.section_id .content-buttons .swiper-button-next {
 width: 6rem;
 height: 6rem;
 box-shadow: 0px 1px 6px #ddd;
}
.section_id a.btn {
  padding: 1.2rem 4.3rem;
}
}
