current.custom_css:
html{
padding-bottom: 0!important;
}
html[dir="rtl"] .header-bottom .main-mn &gt; li:first-child &gt; a:before {
left: -1.5rem;
}
html[dir="ltr"] .header-bottom .main-mn &gt; li:first-child &gt; a:before {
right: -1.5rem;
}
body.template-index {
background-position: center top;
background-repeat: no-repeat;
background-image: url(//cdn.shopify.com/s/files/1/0279/6568/5813/files/slide_bg.jpg?v=1592537785);
}
.template-index #header, .template-index #header .header-top {
background: transparent;
}
.md_call_to_action .bg-image {
background-position: center;
}
.item-blog span.comma {
font-size: 0;
}
.item-blog span.comma:before {
content: '/';
padding: 0 .6rem;
font-size: 1.4rem;
}
.md_blog .read-more {
padding-right: 2.2rem;
border: none;
text-transform: capitalize;
font-weight: 600;
}
.md_blog .read-more:after {
content: '\f112';
opacity: 1;
transform: translateX(0);
background-color: var(--primary);
color: #fff;
border-radius: 1rem;
width: 1.6rem;
height: 1.6rem;
font-size: 1.2rem;
line-height: 1.6rem;
text-align: center;
}
@media (min-width: 576px) and (max-width: 992px){
.modal-dialog {
    max-width: 95%;
}
}
#preview-bar-iframe{
display: none;
}

current.lg_custom_css:
.header.style-01 .control-inner&gt;div a.icon-link{
display: -ms-flexbox;
display: flex;
-ms-flex-align: center;
 align-items: center;
}
.header.style-01 .control-inner&gt;div a.icon-link span.title {
display: inline-block;
margin-left: 2rem;
font-size: 1.3rem;
margin-top: 3px;
}

.header.style-01 .control-inner&gt;div a.icon-link .count {
position: absolute;
top: 0;
right: -.7rem;
line-height: 1.7rem;
width: 1.7rem;
height: 1.7rem;
}

settings.css_section:
.section_id .slideshow__title{
  color: #fff;
  font-size: 1.6rem;
  letter-spacing: -.01em;
  margin-bottom: .5rem;
}
.section_id .slideshow__subtitle{
  color: #fff;
  font-size: 2.4rem;
  line-height: 1.2;
  letter-spacing: -.03em;
}
.section_id .slideshow__description{
  color: #ffd04d;
  font-size: 2.4rem;
  line-height: 1.2;
  letter-spacing: -.03em;
  margin-bottom: 2rem;
}
@media (min-width: 576px){
  .section_id .slideshow__subtitle{
    font-size: 3.6rem;
  }
  .section_id .slideshow__description{
    font-size: 3.6rem;
  }
}
@media (min-width: 768px){
  .section_id .slideshow__subtitle{
    font-size: 4.4rem;
  }
  .section_id .slideshow__description{
    font-size: 4.4rem;
  }
}
