current.custom_css:
.icon-box-content {padding-left:1.3rem;}; 
 .header-bottom padding-left:2rem; .main-mn &gt; li:first-child &gt; a:before {
    left: -1.5rem;
}
.md_products_section .box-tabs a.active {
  color: var(--primary);
  border-bottom-color: var(--primary);
}
.sidebar-wrapper .section-title {
  padding-bottom: 1.3rem;
  border-bottom: 0.1rem solid #ebebeb;
}
.sidebar-wrapper .section-title h3:after {
  content: "";
  position: absolute;
  display: block;
  left: 0;
  right: 0;
  bottom: -1.8rem;
  height: 0.1rem;
  background-color: var(--primary);
}
.group-product .product-form__variants {
  background-color: transparent;
}

current.xl_custom_css:
.main-mn {
  -ms-flex-pack: end;
  justify-content: flex-end;
}
.main-mn .special_link {
  max-width: 545px;
}
.main-mn .hot_link .percen::after {
  left: 80%;
  background-color: #fff;
}

settings.css_section:
.section_id .slideshow__subtitle sup {
  vertical-align: top;
  font-size: 1.3rem;
  top: 1em;
  margin-right: 0;
  letter-spacing: -.01em;
}
@media screen and (min-width: 768px){
  .section_id .slideshow__subtitle sup {
    font-size: 1.6rem;
  }
}
@media screen and (min-width: 992px){
  .section_id .slideshow__subtitle sup {
    font-size: 1.8rem;
  }
}
@media screen and (min-width: 1200px){
  .section_id .slideshow__subtitle sup {
    font-size: 2rem;
  }
}

.section_id .slideshow__title{
  color: #999;
  font-size: 1.4rem;
  letter-spacing: -.01em;
  margin-bottom: .5rem;
}
.section_id .slideshow__subtitle{
  color: #fff;
  font-size: 2.4rem;
  line-height: 1.2;
  letter-spacing: -.03em;
  margin-bottom: .9rem;
}
.section_id .slideshow__subtitle span{
  font-weight: 300;
}
.section_id .slideshow__description{
  color: #fff;
  margin-bottom: 1rem;
  font-size: 1.4rem;
  line-height: 1.3;
}
.section_id .slideshow__description sup {
  top: -.8em;
  font-weight: 400;
  font-size: 1.4rem;
  letter-spacing: -.03em;
}
@media screen and (min-width: 576px){
  .section_id .slideshow__title{
    font-size: 1.6rem;
  }
  .section_id .slideshow__subtitle{
    font-size: 3.6rem;
  }
  .section_id .slideshow__description sup {
    font-size: 1.5rem;
  }
}
@media screen and (min-width: 768px){
  .section_id .slideshow__description{
    line-height: 3.6rem;
    font-size: 2rem;
    letter-spacing: 0.05em;
  }
  .section_id .slideshow__description sup {
    font-size: 1.8rem;
  }
}
@media screen and (min-width: 1200px){
  .section_id .slideshow__description sup {
    font-size: 2rem;
  }
}
